import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer } from 'recharts';
import { BarChart, Bar } from 'recharts';
import { PieChart, Pie, Cell, Legend  } from 'recharts';

const App = () => {
  const [data, setData] = useState(null);
  const [filteredData, setFilteredData] = useState(null);
  const [sortOption, setSortOption] = useState('asc');
  const [token, setToken] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);


  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:3002/api/login', { username, password });

      if (response.status === 200) {
        console.log('Login successful');
        setIsAuthenticated(true); // Assuming you still want to track the authenticated state for UI purposes
        fetchData(); // Fetch data directly after login
      }
    } catch (error) {
      console.error('Login failed:', error);
      setIsAuthenticated(false); // Reset authentication state if login fails
    }
  };
  const handleLogout = () => {
    setIsAuthenticated(false); // Reset authentication state
    setData(null); // Clear data on logout
  };


  const fetchData = async (authToken) => {
    try {
      const result = await axios.get('http://localhost:3002/api/data', {
        headers: { Authorization: authToken }
      });
      setData(result.data);
      setFilteredData(result.data); // Initialize with all data
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    if (isAuthenticated) {
      fetchData(token);
    }
  }, [isAuthenticated]);

  const handleFilter = (e) => {
    const filterValue = e.target.value;
    const newData = {
      ...data,
      lineChart: data.lineChart.filter(val => val.value > parseInt(filterValue)),
    };
    setFilteredData(newData);
  };

  const handleSort = (e) => {
    const sortValue = e.target.value;
    setSortOption(sortValue);
    const sortedData = {
      ...filteredData,
      lineChart: [...filteredData.lineChart].sort((a, b) => sortValue === 'asc' ? a.value - b.value : b.value - a.value),
    };
    setFilteredData(sortedData);
  };

  if (!isAuthenticated) {
    return (
      <div style={{ 
        display: 'flex', 
        flexDirection: 'column', 
        alignItems: 'center', 
        justifyContent: 'center', 
        height: '100vh', 
        backgroundColor: '#f0f2f5'
      }}>
        <div style={{
          background: '#fff',
          padding: '20px',
          borderRadius: '8px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          width: '300px',
          textAlign: 'center'
        }}>
          <h2 style={{ marginBottom: '20px', color: '#333' }}>Login</h2>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            style={{
              width: '100%',
              padding: '10px',
              marginBottom: '10px',
              border: '1px solid #ddd',
              borderRadius: '4px',
              boxSizing: 'border-box'
            }}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{
              width: '100%',
              padding: '10px',
              marginBottom: '20px',
              border: '1px solid #ddd',
              borderRadius: '4px',
              boxSizing: 'border-box'
            }}
          />
          <button
            onClick={handleLogin}
            style={{
              backgroundColor: '#007bff',
              color: '#fff',
              border: 'none',
              padding: '10px 20px',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '16px',
              width: '100%'
            }}
          >
            Login
          </button>
        </div>
      </div>
    );

    // return (
    //   <div style={{ padding: '20px' }}>
    //     <h2>Login</h2>
    //     <input
    //       type="text"
    //       placeholder="Username"
    //       value={username}
    //       onChange={(e) => setUsername(e.target.value)}
    //     />
    //     <input
    //       type="password"
    //       placeholder="Password"
    //       value={password}
    //       onChange={(e) => setPassword(e.target.value)}
    //     />
    //     <button onClick={handleLogin}>Login</button>
    //   </div>
    // );
  }

  if (!filteredData) return <div>Loading...</div>;

  return (
    <div style={{ padding: '20px' ,margin:'50px'}}>
      <h1>Dashboard</h1>
      <button onClick={handleLogout}>Logout</button>

      <div style={{ marginBottom: '20px' }}>
        <label>Filter by Value:</label>
        <select onChange={handleFilter}>
          <option value="0">Show All</option>
          <option value="20">Greater than 20</option>
          <option value="30">Greater than 30</option>
        </select>

        <label>Sort by Value:</label>
        <select onChange={handleSort}>
          <option value="asc">Ascending</option>
          <option value="desc">Descending</option>
        </select>
      </div>

      <div style={{ marginBottom: '20px' }}>
        <h2>Line Chart</h2>
        <ResponsiveContainer width="50%" height={300}>
          <LineChart data={filteredData.lineChart}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="month" />
            <YAxis />
            <Tooltip />
            <Line type="monotone" dataKey="value" stroke="#8884d8" />
          </LineChart>
        </ResponsiveContainer>
      </div>

      <div style={{ marginBottom: '20px' }}>
        <h2>Bar Chart</h2>
        <ResponsiveContainer width="50%" height={300}>
          <BarChart data={filteredData.barChart}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="category" />
            <YAxis />
            <Tooltip />
            <Bar dataKey="value" fill="#82ca9d" />
          </BarChart>
        </ResponsiveContainer>
      </div>


      <div style={{ marginBottom: '20px' }}>
  <h2>Pie Chart</h2>
  <ResponsiveContainer width="50%" height={300}>
    <PieChart>
      <Pie
        data={filteredData.pieChart}
        dataKey="value"
        nameKey="segment"
        outerRadius={100}
        fill="#8884d8"
        label={({ percent }) => `${(percent * 100).toFixed(0)}%`}
      >
        {filteredData.pieChart.map((entry, index) => (
          <Cell key={`cell-${index}`} fill={`#${Math.floor(Math.random() * 16777215).toString(16)}`} />
        ))}
      </Pie>
      <Legend />
    </PieChart>
  </ResponsiveContainer>
</div>
    </div>
  );
};

export default App;
